jQuery(document).ready(function($){
	$('.advgb-images-slider').each(function(){
		$(this).children().each(function(){
			$(this).css('display','none')
		})	

		$(this).children().eq(0).css('display','flex');

		this.onwheel = function(event){
	    	let y = event.deltaY;
	    	var enable = -1;
	    	if(y > 0)
	    	{
	    		$(this).find('.advgb-image-slider-item').each(function(){
		    		if(enable == 0)
		    		{
		    			$(this).css('display','flex');
		    			enable = 1;
		    		}

		    		if($(this).css('display') == 'flex' && enable == -1)
		    		{
		    			enable = 0;
		    			$(this).css('display','none');
		    		}
		    	})

		    	if(enable != 1)
		    	{
		    		$(this).children().eq(0).css('display','flex');
		    		if(enable == 0)
		    		{
		    			return true;
		    		}
		    	}
		        return false;	
	    	}
	    	else
	    	{
	    		let index = 0;
	    		let item = 0;
	    		$(this).find('.advgb-image-slider-item').each(function(){
	    			if($(this).css('display') == 'flex')
	    			{
	    				index = item;
	    				$(this).css('display','none');
	    			}
	    			item++;
	    		})

	    		if(index == 0)
	    		{
	    			$(this).children().eq(item -1).css('display','flex');
	    			return true;
	    		}
	    		else
	    		{
	    			$(this).children().eq(index - 1).css('display','flex');
	    			return false;
	    		}
	    	}
	    }
	})

	
	// const el = document.querySelector('.advgb-images-slider');
	// if(el)
	// {
		
	// }	
    
})

